package fifth.longest_substring

import org.scalatest.funspec.AnyFunSpec

/**
 *  rintln(getLongestSubstring("ab"))
 *  println(getLongestSubstring("abc"))
 *  println(getLongestSubstring("aba"))
 *  println(getLongestSubstring("abcda"))
 *  println(getLongestSubstring("abcda23c"))
 *  println(getLongestSubstring("abcda23c3523a"))
 *  println(getLongestSubstring(""))
 *  de
 */
class LongestPalindromicSubstringTest extends AnyFunSpec{
  describe("Fifth:LongestPalindromicSubstring") {
    it("1") {
      assert(LongestPalindromicSubstring.getLongestSubstring("ab") == "a")
    }
    it("2") {
      assert(LongestPalindromicSubstring.getLongestSubstring("abc") == "a")
    }
    it("3") {
      assert(LongestPalindromicSubstring.getLongestSubstring("a") == "aa")
    }
    it("4") {
      assert(LongestPalindromicSubstring.getLongestSubstring("aba") == "aba")
    }
    it("5") {
      assert(LongestPalindromicSubstring.getLongestSubstring("abcda") == "abcda")
    }
    it("6") {
      assert(LongestPalindromicSubstring.getLongestSubstring("abcda23c") == "cda23c")
    }
    it("7") {
      assert(LongestPalindromicSubstring.getLongestSubstring("abcda23c3523a") == "a23c3523a")
    }
    it("8") {
      assert(LongestPalindromicSubstring.getLongestSubstring("") == "")
    }
  }
}
